import { Component, Input } from '@angular/core';
import { UtilsService } from '../services/utils.service';

@Component({
  selector: 'app-modal-edit-item',
  templateUrl: './modal-edit-item.component.html',
  styleUrls: ['./modal-edit-item.component.sass']
})
export class ModalEditItemComponent {
  utils: UtilsService = new UtilsService();
  showModal: boolean = false;
  @Input() item: any;

  toggle() {
    this.showModal = !this.showModal;
  }
  saveItem(title: any, description: any, checkbox: any) {
    this.item.title = title.value.trim();
    this.item.description = description.value.trim();
    if (checkbox.checked) {
      if (!this.item.finished_at) {
        this.item.finished_at = this.utils.generateDate();
      }
    } else {
      this.item.finished_at = null;
    }
    this.toggle();
  }
  deleteItem() {
    this.toggle();
  }
  getDate(e: any) {
    console.log(e.value);
  }
}
