import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-new-todo',
  templateUrl: './new-todo.component.html',
  styleUrls: ['./new-todo.component.sass']
})
export class NewTodoComponent {
  date = new Date();
  dateObj = {
    day: this.leadingZeros(2, this.date.getDate()),
    month: this.leadingZeros(2, this.date.getMonth()),
    year: this.leadingZeros(2, this.date.getFullYear())
  }
  todoItem: any = {
    title: "",
    description: "",
    created_at: `${this.dateObj.day}/${this.dateObj.month}/${this.dateObj.year}`
  }
  @Output() newTodo = new EventEmitter();
  createNewTodoItem() {
    this.newTodo.emit(this.todoItem);
  };
  leadingZeros(amount: number, value: number | string) {
    return value.toString().padStart(amount, "0");
  }
}
