import { Component } from '@angular/core';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.sass']
})
export class ModalComponent {
  showModal: boolean = false;

  toggle() {
    this.showModal = !this.showModal;
  }
}
