import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class TodoItemsService {
  constructor(private httpClient: HttpClient) { }
  refreshStatus = +new Date();
  oldRefresh: number;

  API_URL: string = 'http://localhost:5203/api/todo-items';

  // static todoItems = [
  //   {
  //     title: "Walk with the dog",
  //     description: "Take the dog on a long walk because it needs to get rid of all the fatness",
  //     createdAt: "2024-12-23",
  //     completedAt: null,
  //     estimatedAt: "2024-12-20"
  //   },
  //   {
  //     title: "Drink water",
  //     description: "Get hydrated",
  //     createdAt: "2024-12-21",
  //     completedAt: "2024-12-20",
  //     estimatedAt: "2024-12-20"
  //   },
  //   {
  //     title: "Finish project",
  //     description: "Zella project",
  //     createdAt: "2024-12-22",
  //     completedAt: "2024-12-20",
  //     estimatedAt: "2024-12-20"
  //   }
  // ];

  // 'http://localhost:5203/api/todo-items'

  getTodoItems() {
    return this.httpClient.get<any>(this.API_URL);
  }

  updateTodoItem(item: any) {
    this.refreshPage()
    return this.httpClient.put<any>(`${this.API_URL}/${item.id}`, item);
  }

  deleteTodoItem(item: any) {
    this.refreshPage()
    return this.httpClient.delete<any>(`${this.API_URL}/${item.id}`);
  }

  createTodoItem(item: any) {
    this.refreshPage()
    return this.httpClient.post<any>(`${this.API_URL}`, item)
  }

  refreshPage() {
    this.oldRefresh = this.refreshStatus;
    this.refreshStatus = +new Date();
  }

  // getTodoItems() {
  //   let items = this.ascendingDateSort(TodoItemsService.todoItems)
  //   return items;
  // }

  ascendingDateSort(items: any) {
    let sorted = items.sort((a: any, b: any) => { return (new Date(a.createdAt).getTime()) - (new Date(b.createdAt)).getTime(); })
    return sorted;
  }

  descendingDateSort(items: any) {
    let sorted = items.sort((a: any, b: any) => { return (new Date(b.createdAt).getTime()) - (new Date(a.createdAt)).getTime(); })
    return sorted;
  }
}
