import { Component, EventEmitter, Output } from '@angular/core';
import { UtilsService } from '../services/utils.service';

@Component({
  selector: 'app-new-todo',
  templateUrl: './new-todo.component.html',
  styleUrls: ['./new-todo.component.sass']
})
export class NewTodoComponent {
  utils: UtilsService = new UtilsService();

  todoItem: any = {
    title: "",
    description: "",
    created_at: this.utils.generateDate()
  }

  @Output() newTodo = new EventEmitter();

  createNewTodoItem() {
    let trimStr = this.todoItem.title.trim();
    if (trimStr != "") {
      this.todoItem.title = trimStr;
      this.newTodo.emit(this.utils.deepCopy(this.todoItem));
    }
    this.todoItem.title = ""
  }
}
