import { EventEmitter, Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class TodoItemsService {
  constructor() { }

  itemsList = new EventEmitter<any>();

  todoItems = [
    {
      title: "Walk with the dog",
      description: "Take the dog on a long walk because it needs to get rid of all the fatness",
      created_at: "20/12/2024",
      finished_at: null,
      estimated_date: "20/12/2024"
    },
    {
      title: "Drink water",
      description: "Get hydrated",
      created_at: "20/12/2024",
      finished_at: "20/12/2024",
      estimated_date: "20/12/2024"
    },
    {
      title: "Finish project",
      description: "Zella project",
      created_at: "20/12/2024",
      finished_at: "20/12/2024",
      estimated_date: "20/12/2024"
    }
  ];

  addNewTodo(event: any) {
    let item = event;
    this.todoItems.unshift(item);
  }

  getTodoItems() {
    this.itemsList.emit(todoItems)
  }
}
