import { EventEmitter, Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class TodoItemsService {
  constructor() { }

  static todoItems = [
    {
      title: "Walk with the dog",
      description: "Take the dog on a long walk because it needs to get rid of all the fatness",
      created_at: "2024-12-23",
      finished_at: null,
      estimated_date: "2024-12-20"
    },
    {
      title: "Drink water",
      description: "Get hydrated",
      created_at: "2024-12-21",
      finished_at: "2024-12-20",
      estimated_date: "2024-12-20"
    },
    {
      title: "Finish project",
      description: "Zella project",
      created_at: "2024-12-22",
      finished_at: "2024-12-20",
      estimated_date: "2024-12-20"
    }
  ];

  // 'http://localhost:5203/api/todo-items'

  addNewTodo(event: any) {
    let item = event;
    TodoItemsService.todoItems.unshift(item);
  }

  getTodoItems() {
    let items = this.ascendingDateSort(TodoItemsService.todoItems)
    return items;
  }

  ascendingDateSort(items: any) {
    let sorted = items.sort((a: any, b: any) => { return (new Date(a.created_at).getTime()) - (new Date(b.created_at)).getTime(); })
    return sorted;
  }

  descendingDateSort(items: any) {
    let sorted = items.sort((a: any, b: any) => { return (new Date(b.created_at).getTime()) - (new Date(a.created_at)).getTime(); })
    return sorted;
  }
}
