import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.sass']
})
export class TodoItemComponent {
  @Input() item: any = {};
  date = new Date();
  dateObj = {
    day: this.leadingZeros(2, this.date.getDate()),
    month: this.leadingZeros(2, this.date.getMonth()),
    year: this.leadingZeros(2, this.date.getFullYear())
  };
  todoItemClick() {
    if (!this.item.finished_at) {
      this.item.finished_at = `${this.dateObj.day}/${this.dateObj.month}/${this.dateObj.year}`;
    }
  };
  leadingZeros(amount: number, value: number | string) {
    return value.toString().padStart(amount, "0");
  }
}
